import React, {useState} from 'react';
import './App.css';
import Header from './components/Header';
import AddTodo from './components/AddTodo';
import Todos from './components/Todos';
import Footer from './components/Footer';

const App = () => {
  const onDelete = (todo) => {
    console.log("I am on Dlete function" , todo)
    setTodos(todos.filter((e)=>{
      return e!==todo
    }))
  };

  const [todos , setTodos] = useState([
    {
      sno: 1,
      title:"Have to go to the market..",
      desc:"Be careful You Have to go to the market as soon as possible"
    },
    {
      sno: 2,
      title:"Have to go to the School..",
      desc:"Be careful You Have to go to the market as soon as possible"
    },
    {
      sno: 3,
      title:"Have to go to the Tution..",
      desc:"Be careful You Have to go to the market as soon as possible"
    },
    {
      sno: 4,
      title:"Have to go to the gym..",
      desc:"Be careful You Have to go to the gym as soon as possible"
    }
  ])
  return (
    <>
      <Header/>
      <AddTodo/>
      <Todos todos={todos} onDelete={onDelete}/>
      <Footer/>
      </>
  );
}

export default App;
