import React from 'react';
const TodoItem = ({todo, onDelete}) => {
    return(
        <div className="item">
            <h1 className="title">{todo.title}</h1>
            <p className="desc">{todo.desc}</p>
            <button onClick={()=>{onDelete(todo)}}>Delete</button>
       </div>
    )
}
 
export default TodoItem;
